// vue3引入的不再是Vue构造函数，引入的是一个名为createApp的工厂函数（构造函数需要通过new调用，工厂函数不需要new可以直接调用）
import { createApp } from 'vue'
import App from './App.vue'

createApp(App).mount('#app')

// vue2写法
const vm = new Vue({
  render: h => h(App)
})
vm.$mount('#app')
